function tima(e){let n,t,r,s;const i=e=>0!=e?e:"";e=new Date(e).getTime(),isNaN(e)||setInterval(function(){let m=new Date;m=m.getTime();let a=parseInt((e-m)/1e3);if(!(a>=0))return;n=parseInt(a/86400),a%=86400,t=parseInt(a/3600),a%=3600,r=parseInt(a/60),a%=60,s=parseInt(a),document.getElementById("days").innerHTML=i(n),document.getElementById("hours").innerHTML=i(t),document.getElementById("minutes").innerHTML=i(r),document.getElementById("seconds").innerHTML=i(s)},1e3)}
